$(document).ready(function () {
    /*
     date store today date.
     d store today date.
     m store current month.
     y store current year.
     */
    "use strict";
    var date = new Date();
    var d = date.getDate();
    var m = date.getMonth();
    var y = date.getFullYear();
    /*
     Initialize fullCalendar and store into variable.
     Why in variable?
     Because doing so we can use it inside other function.
     In order to modify its option later.
     */
    var startTime, endTime, room_number, block;
    room_number = $('#roomnumber').text();
    block = $('#blockname').text();
    $('#squarespaceModal').find("#room_number").val(room_number);
    $('#squarespaceModal').find("#block_name").val(block);
    document.getElementById("room_number").readOnly = true;
    document.getElementById("block_name").readOnly = true;

    var calendar = $('#calendar').fullCalendar(
        {
            /*
             header option will define our calendar header.
             left define what will be at left position in calendar
             center define what will be at center position in calendar
             right define what will be at right position in calendar
             */
            header: {
                left: 'prev,next today',
                center: 'title',
                right: 'month,agendaWeek,agendaDay'
            },
            /*
             defaultView option used to define which view to show by default,
             for example we have used agendaWeek.
             */
            defaultView: 'agendaWeek',
            /*
             selectable:true will enable user to select datetime slot
             selectHelper will add helpers for selectable.
             */
            selectable: true,
            selectHelper: true,
            /*
             when user select timeslot this option code will execute.
             It has three arguments. Start,end and allDay.
             Start means starting time of event.
             End means ending time of event.
             allDay means if events is for entire day or not.
             */
            select: function (start, end, allDay) {
                /*
                 if title is enterd calendar will add title and event into fullCalendar.
                 */
                startTime = convert(start);
                endTime = convert(end);
                $('#squarespaceModal').find("#startDateTime").val(startTime);
                $('#squarespaceModal').find("#endDateTime").val(endTime);
                $('#squarespaceModal').modal({
                    show: true
                });
                calendar.fullCalendar('unselect');
            },
            /*
             editable: true allow user to edit events.
             */
            currentTimezone: 'Asia/Kolkata',
            editable: true,
            allDaySlot: false,
            eventLimit: true,
            /*
             events is the main option for calendar.
             for demo we have added predefined events in json object.
             */
            events: function (start, end, callback) {
                $.ajax({
                    url: "/eventlist",
                    type: 'Get',
                    data: "start=" + Math.round(start.getTime() / 1000) + "&end=" + Math.round(end.getTime() / 1000) + "&_rand=" + Math.floor(Math.random() * 100),
                    datatype: "json",
                    contentType: "application/json",
                    success: function (data) {
                        var eventsToShow = [];
                        for (var i = 0; i < data.length; i++) {
                            if (data[i].room_number == room_number) {
                                eventsToShow.push(data[i]);
                            }
                        }
                        if (callback) {
                            console.log(eventsToShow);
                            callback(eventsToShow);
                        } else {
                            console.log("callback is not defined");
                        }
                    }
                });
            }
        });
    $('#saveEvent').on('click', function (e) {
        // We don't want this to act as a link so cancel the link action
        e.preventDefault();
        doSubmit();
    });
    function convert(str) {
        var mnths = {
                Jan: "01", Feb: "02", Mar: "03", Apr: "04", May: "05", Jun: "06",
                Jul: "07", Aug: "08", Sep: "09", Oct: "10", Nov: "11", Dec: "12"
            },
            date = str.toString().split(" ");

        return ([date[3], mnths[date[1]], date[2]].join("-") + " " + date[4]);
    }

    function doSubmit() {
        console.log("this ");

        var title = $('#squarespaceModal').find('#description').val(), granter, reserved_by;
        granter = $("#squarespaceModal").find("#granter").val();
        reserved_by = $("#squarespaceModal").find("#reserved_by").val();
        startTime = $('#squarespaceModal').find("#startDateTime").val();
        endTime = $('#squarespaceModal').find("#endDateTime").val();
        if (title) {
            $("#calendar").fullCalendar('renderEvent',
                {
                    title: $('#squarespaceModal').find('#description').val(),
                    start: startTime,
                    end: endTime,
                    allDay: false,
                    overlap: false
                },
                true
            );
            $.ajaxSetup({
                beforeSend: function (xhr, settings) {
                    if (!csrfSafeMethod(settings.type) && !this.crossDomain) {
                        xhr.setRequestHeader("X-CSRFToken", csrftoken);
                    }
                }
            });
            var today = new Date();
            var date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
            var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
            var dateTime = date + ' ' + time;
            var data = {
                "description": title,
                "startDateTime": startTime,
                "endDateTime": endTime,
                "room_number": room_number,
                "granter": granter,
                "block": block,
                "sender":''
            };
            console.log(data);
            $.ajax({
                url: '/saveEvents',
                data: data,
                type: "POST",
                success: function (json) {
                    console.log(json);
                    $("#squarespaceModal").modal('hide');
                }
            });
        }
    }

    // using jQuery
    function getCookie(name) {
        var cookieValue = null;
        if (document.cookie && document.cookie != '') {
            var cookies = document.cookie.split(';');
            for (var i = 0; i < cookies.length; i++) {
                var cookie = jQuery.trim(cookies[i]);
                // Does this cookie string begin with the name we want?
                if (cookie.substring(0, name.length + 1) == (name + '=')) {
                    cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                    break;
                }
            }
        }
        return cookieValue;
    }

    var csrftoken = getCookie('csrftoken');

    function csrfSafeMethod(method) {
        // these HTTP methods do not require CSRF protection
        return (/^(GET|HEAD|OPTIONS|TRACE)$/.test(method));
    }

    $.ajaxSetup({
        beforeSend: function (xhr, settings) {
            if (!csrfSafeMethod(settings.type) && !this.crossDomain) {
                xhr.setRequestHeader("X-CSRFToken", csrftoken);
            }
        }
    });
})
;